 id
,sequence
,stnd_date
,item_code
,note
,income
,expenditure
,balance
)
VALUES

     #{item.id}
    ,#{item.sequence}
    ,#{item.stndDate}
    ,#{item.itemCode}
    ,#{item.note}
    ,#{item.income}
    ,#{item.expenditure}
    ,#{item.balance}

ON DUPLICATE KEY UPDATE
 sequence    = values(sequence)
,stnd_date   = values(stnd_date)
,item_code   = values(item_code)
,note        = values(note)
,income      = values(income)
,expenditure = values(expenditure)
,balance     = values(balance)
